// Disable direct access
defined( 'ABSPATH' ) or die( 'GTFO' );



// change login link
add_filter('login_headerurl','strl_login_custom_link');
function strl_login_custom_link() {
  return 'https://stuurlui.nl';
}


// change login header title
add_filter('login_headertitle', 'strl_login_logo_title');
function strl_login_logo_title() {
  return 'Stuurlui Online Marketing';
}



// change login look&feel
add_action( 'login_enqueue_scripts', 'strl_login_look_feel' );
function strl_login_look_feel() { 
  global $strl_plugin_path;
  ?>
  <style type="text/css">
    .login h1 a {
      background-image: url('data:image/png;base64,')!important;
      width: 100%!important;
      background-size: auto 100px!important;
      height: 100px!important;
    }
    body.login{
      background: url('<?php echo $strl_plugin_path ?>img/background-strl.jpg') no-repeat center center fixed; 
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: cover;
      background-color: #333!important;
    }
    .login .message {
      border-left: 0;
      background-color: #4A586B;
      -webkit-box-shadow: 0 1px 1px 0 rgba(0,0,0,.1);
      color: #fff;
      box-shadow: 0 1px 1px 0 rgba(0,0,0,.1);
      text-shadow: 0 1px 0 rgba(33, 46, 63, 1);
    }
    .login .button-primary {
      background: #4A586B;
      -webkit-box-shadow: none;
      box-shadow: none;
      color: #fff;
      border-color: rgba(33, 46, 63, 1);
      border-radius: 0;
      text-decoration: none;
      padding: 3px 12px!important;
      height: auto!important;
      width: auto!important;
      text-shadow: 0 1px 0 rgba(33, 46, 63, 1);
    }
    .login #backtoblog a, .login #nav a{
      color: #4A586B;
    }
  </style>
<?php }




// Change notification on login attempt to generic one
function strl_failed_login() {
    return "Uw inloggegevens zijn niet correct";
}
add_filter ( 'login_errors', 'strl_failed_login' );